mapper.select:

	select a.no ,a.name ,a.desc, if(count is null , 0 , count) count 
	from ( 
					select c1.no,c1.name,c1.desc from category c1 where c1.blog_id = #{id }) as a
	left outer join (	
					select p.category_no, count(*) count from post p group by p.category_no) as b on a.no = b.category_no ;
